using System;\nusing System.DirectoryServices.Protocols;\nusing System.Net;\n\npublic class LdapSearch\n{\n    public static void Main(string[] args)\n    {\n        string ldapServer = "yourLdapServer";\n        string userDn = "uid=DaffyD,OU=Ducks,OU=People,O=A";\n        string userName = "yourUserName";\n        string password = "yourPassword";\n\n        LdapConnection ldapConnection = new LdapConnection(ldapServer);\n        NetworkCredential credential = new NetworkCredential(userName, password);\n        ldapConnection.Credential = credential;\n        ldapConnection.AuthType = AuthType.Basic;\n        \n        ldapConnection.Bind();\n\n        SearchRequest searchRequest = new SearchRequest(\n            "O=A", // Base DN\n            "(member=" + userDn + ")", // Filter to find groups the user is a member of\n            SearchScope.Subtree, \n            new string[] { "distinguishedName" } // Attributes to retrieve\n        );\n\n        SearchResponse searchResponse = (SearchResponse)ldapConnection.SendRequest(searchRequest);\n\n        foreach (SearchResultEntry entry in searchResponse.Entries)\n        {\n            Console.WriteLine("Group DN: " + entry.DistinguishedName);\n        }\n    }\n}