using WatiN.Core;\n\n// Initialize IE instance\nvar ie = new IE("http://your-url.com");\n\n// Wait for the TabContainer to be fully loaded\nie.WaitForComplete();\n\n// Locate the tab header by its ID or any other suitable selector\nvar tabHeader = ie.Element(Find.ById("tabHeaderID"));\n\n// Click the tab header to activate the tab\ntabHeader.Click();\n\n// Alternatively, you can trigger the event directly\ntabHeader.FireEvent("onclick");\n\n// Close the browser instance if needed\nie.Close();