using MySql.Data.MySqlClient;\n\nstring connectionString = "your_connection_string";\nstring updateQuery = "UPDATE your_table SET expires = @expires WHERE user_id = @user_id";\n\nusing (MySqlConnection connection = new MySqlConnection(connectionString))\n{\n    using (MySqlCommand command = new MySqlCommand(updateQuery, connection))\n    {\n        // Set the user_id parameter\n        command.Parameters.AddWithValue("@user_id", userId);\n\n        // Set the expires parameter to DBNull.Value to set it to the default value (NULL)\n        command.Parameters.AddWithValue("@expires", DBNull.Value);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}