using System;\nusing System.Windows.Forms;\n\npublic class PaddingExample\n{\n    public static void Main()\n    {\n        string str1 = "Test".PadRight(20);\n        string str2 = "Test123".PadRight(20);\n\n        string common = "Add this text";\n\n        string result = str1 + common + "\n" + str2 + common;\n\n        // Create a new form\n        Form form = new Form();\n        form.Text = "Padding Example";\n\n        // Create a new label with a monospaced font\n        Label label = new Label();\n        label.Font = new System.Drawing.Font("Consolas", 10);\n        label.Text = result;\n        label.AutoSize = true;\n\n        // Add the label to the form\n        form.Controls.Add(label);\n\n        // Display the form\n        Application.Run(form);\n    }\n}