using System;\nusing System.Net;\nusing Microsoft.Reporting.WebForms;\n\npublic byte[] RenderReportToPdf(string reportPath, string reportServerUrl, NetworkCredential credentials)\n{\n    ReportExecutionService rs = new ReportExecutionService();\n    rs.Credentials = credentials;\n    rs.Url = reportServerUrl;\n\n    string format = "PDF";\n    string devInfo = "<DeviceInfo><Toolbar>False</Toolbar></DeviceInfo>";\n    string historyID = null;\n    string encoding;\n    string mimeType;\n    string extension;\n    Warning[] warnings = null;\n    string[] streamIDs = null;\n\n    ExecutionHeader execHeader = new ExecutionHeader();\n    rs.ExecutionHeaderValue = execHeader;\n    rs.LoadReport(reportPath, historyID);\n\n    ExecutionInfo execInfo = rs.GetExecutionInfo();\n\n    byte[] result = rs.Render(format, devInfo, out extension, out mimeType, out encoding, out warnings, out streamIDs);\n    \n    return result;\n}