using System;\nusing System.Data.SqlClient;\n\npublic DateTime GetLatestRunDateFromSqlServer(string connectionString)\n{\n    DateTime latestRunDate = DateTime.MinValue;\n    string query = "SELECT MAX(RunDate) FROM ProductionRuns";\n\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        SqlCommand command = new SqlCommand(query, connection);\n        connection.Open();\n        object result = command.ExecuteScalar();\n        if (result != DBNull.Value)\n        {\n            latestRunDate = (DateTime)result;\n        }\n    }\n\n    return latestRunDate;\n}