using System.Data;\nusing System.Data.SqlClient;\n\nstring connectionString = "your_connection_string";\nstring insertCommand = "INSERT INTO YourTable (Column1, Column2) VALUES (@Value1, @Value2)";\n\nDataTable dataTable = new DataTable();\ndataTable.Columns.Add("Column1", typeof(int));\ndataTable.Columns.Add("Column2", typeof(string));\n\n// Populate the DataTable with your data\ndataTable.Rows.Add(1, "Data1");\ndataTable.Rows.Add(2, "Data2");\n// Add more rows as needed\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    SqlDataAdapter dataAdapter = new SqlDataAdapter();\n    dataAdapter.InsertCommand = new SqlCommand(insertCommand, connection);\n    dataAdapter.InsertCommand.Parameters.Add("@Value1", SqlDbType.Int, 0, "Column1");\n    dataAdapter.InsertCommand.Parameters.Add("@Value2", SqlDbType.NVarChar, 50, "Column2");\n\n    // Set the batch size\n    dataAdapter.UpdateBatchSize = 100; // Adjust the batch size as needed\n\n    connection.Open();\n    dataAdapter.Update(dataTable);\n    connection.Close();\n}