protected void Page_Load(object sender, EventArgs e)\n   {\n       var jobId = Session["JobId"] as string;\n       if (jobId != null)\n       {\n           var state = Hangfire.JobStorage.Current.GetMonitoringApi().JobDetails(jobId).History.FirstOrDefault()?.StateName;\n           if (state == "Succeeded")\n           {\n               Response.Redirect("TaskCompleted.aspx");\n           }\n       }\n   }