using System;\nusing System.Xml;\nusing System.Xml.Xsl;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load the XML document\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load("path_to_your_xml_file.xml");\n\n        // Load the XSLT stylesheet\n        XslCompiledTransform xslt = new XslCompiledTransform();\n        xslt.Load("path_to_your_xslt_file.xslt");\n\n        // Perform the transformation and output to a file\n        using (XmlWriter writer = XmlWriter.Create("output.csv"))\n        {\n            xslt.Transform(xmlDoc, writer);\n        }\n\n        Console.WriteLine("Transformation complete. Check the output.csv file.");\n    }\n}