using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFilePath = "path/to/your/input.pdf";\n        string outputFilePath = "path/to/your/output.pdf";\n\n        // Open the existing PDF document\n        PdfReader pdfReader = new PdfReader(inputFilePath);\n        using (FileStream outputStream = new FileStream(outputFilePath, FileMode.Create, FileAccess.Write))\n        {\n            // Create a PdfStamper to modify the PDF\n            PdfStamper pdfStamper = new PdfStamper(pdfReader, outputStream);\n\n            // Modify the document properties\n            pdfStamper.MoreInfo = new System.Collections.Generic.Dictionary<string, string>\n            {\n                { "Title", "New Title" },\n                { "Author", "New Author" },\n                { "Subject", "New Subject" }\n            };\n\n            // Close the PdfStamper\n            pdfStamper.Close();\n        }\n\n        // Close the PdfReader\n        pdfReader.Close();\n\n        Console.WriteLine("PDF properties modified successfully!");\n    }\n}