using System.Windows;\nusing System.Windows.Media;\n\nnamespace WpfApp\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n            AdjustFontSizes(this.MainGrid, 3.779);\n        }\n\n        private void AdjustFontSizes(FrameworkElement element, double scaleFactor)\n        {\n            foreach (var child in LogicalTreeHelper.GetChildren(element))\n            {\n                if (child is FrameworkElement fe)\n                {\n                    if (fe is TextBlock textBlock)\n                    {\n                        textBlock.FontSize /= scaleFactor;\n                    }\n                    else\n                    {\n                        AdjustFontSizes(fe, scaleFactor);\n                    }\n                }\n            }\n        }\n    }\n}