public class Backer\n{\n    public virtual Guid BackerId { get; set; }\n    public virtual string Name { get; set; }\n    public virtual Guid PostCardSizeId { get; set; }\n    public virtual string ItemNumber { get; set; }\n    public virtual IList<BackerEntry> BackerEntries { get; set; }\n\n    public Backer()\n    {\n        BackerEntries = new List<BackerEntry>();\n    }\n\n    public virtual void AddBackerEntry(BackerEntry entry)\n    {\n        entry.Backer = this;\n        BackerEntries.Add(entry);\n    }\n}