using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class DPAPIHelper\n{\n    public static byte[] EncryptStringToBytes(string plainText)\n    {\n        return ProtectedData.Protect(Encoding.UTF8.GetBytes(plainText), null, DataProtectionScope.CurrentUser);\n    }\n\n    public static string DecryptStringFromBytes(byte[] cipherText)\n    {\n        return Encoding.UTF8.GetString(ProtectedData.Unprotect(cipherText, null, DataProtectionScope.CurrentUser));\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string sensitiveData = "your_sql_password";\n        byte[] encryptedData = DPAPIHelper.EncryptStringToBytes(sensitiveData);\n        \n        // Store `encryptedData` securely, e.g., in a file or registry.\n        \n        // When needed, decrypt the data.\n        string decryptedData = DPAPIHelper.DecryptStringFromBytes(encryptedData);\n        \n        Console.WriteLine("Decrypted data: " + decryptedData);\n    }\n}