using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string processName = "notepad.exe"; // Replace with your process name\n\n        WqlEventQuery query = new WqlEventQuery(\n            $"SELECT * FROM Win32_ProcessStartTrace WHERE ProcessName = '{processName}'");\n\n        ManagementEventWatcher watcher = new ManagementEventWatcher(query);\n        watcher.EventArrived += new EventArrivedEventHandler(ProcessStarted);\n        watcher.Start();\n\n        Console.WriteLine($"Waiting for {processName} to start...");\n        Console.ReadLine(); // Keep the application running\n        watcher.Stop();\n    }\n\n    static void ProcessStarted(object sender, EventArrivedEventArgs e)\n    {\n        Console.WriteLine("Process started: " + e.NewEvent.Properties["ProcessName"].Value);\n    }\n}