using System;\nusing System.Runtime.InteropServices;\n\n[StructLayout(LayoutKind.Sequential, CharSet = CharSet.Ansi)]\npublic struct FixedLengthRecord\n{\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 15)]\n    public string FirstName;\n\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 20)]\n    public string LastName;\n\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 30)]\n    public string Address;\n\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 20)]\n    public string City;\n\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 2)]\n    public string State;\n\n    [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 10)]\n    public string Zip;\n\n    public override string ToString()\n    {\n        return $"{FirstName.PadRight(15)}{LastName.PadRight(20)}{Address.PadRight(30)}{City.PadRight(20)}{State.PadRight(2)}{Zip.PadRight(10)}";\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        FixedLengthRecord record = new FixedLengthRecord\n        {\n            FirstName = "John",\n            LastName = "Doe",\n            Address = "123 Main St",\n            City = "Anytown",\n            State = "CA",\n            Zip = "12345"\n        };\n\n        string fixedLengthString = record.ToString();\n        Console.WriteLine(fixedLengthString);\n    }\n}