using System;\nusing System.Diagnostics;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        foreach (Process process in Process.GetProcessesByName("java"))\n        {\n            Console.WriteLine($"Process ID: {process.Id}");\n            string query = $"SELECT CommandLine, ExecutablePath, WorkingSetSize FROM Win32_Process WHERE ProcessId = {process.Id}";\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(query);\n            \n            foreach (ManagementObject obj in searcher.Get())\n            {\n                Console.WriteLine($"Command Line: {obj["CommandLine"]}");\n                Console.WriteLine($"Executable Path: {obj["ExecutablePath"]}");\n                Console.WriteLine($"Working Set Size: {obj["WorkingSetSize"]}");\n            }\n        }\n    }\n}