using System;\nusing EnvDTE;\nusing EnvDTE80;\nusing System.Runtime.InteropServices;\n\npublic class SolutionDirectoryHelper\n{\n    public static string GetSolutionDirectory()\n    {\n        // Get the currently running Visual Studio IDE instance\n        DTE2 dte = (DTE2)Marshal.GetActiveObject("VisualStudio.DTE.16.0"); // Version number might need to be adjusted\n\n        if (dte != null && dte.Solution != null && dte.Solution.FullName != null)\n        {\n            // Get the full path of the solution file\n            string solutionFullName = dte.Solution.FullName;\n            \n            // Extract the directory from the solution path\n            return System.IO.Path.GetDirectoryName(solutionFullName);\n        }\n\n        return null;\n    }\n}