public partial class UserControl1 : UserControl\n{\n    private MyBizObject myBizObject;\n\n    public UserControl1()\n    {\n        InitializeComponent();\n        // Initial setup\n        myBizObject = new MyBizObject();\n        errorProvider1.DataSource = null; // Disable automatic validation initially\n    }\n\n    private void SaveButton_Click(object sender, EventArgs e)\n    {\n        // Set the DataSource to the business object to enable validation\n        errorProvider1.DataSource = myBizObject;\n\n        // Trigger validation\n        ValidateChildren();\n\n        // Check for validation errors before saving\n        if (string.IsNullOrEmpty(errorProvider1.GetError(yourControl)))\n        {\n            myBizObject.Save();\n            MessageBox.Show("Save successful!");\n        }\n        else\n        {\n            MessageBox.Show("Please correct the errors before saving.");\n        }\n    }\n}