using System;\n\npublic partial class DisplayPDF : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            ltlPDF.Text = string.Empty;\n        }\n    }\n\n    protected void ddlPDFs_SelectedIndexChanged(object sender, EventArgs e)\n    {\n        string selectedValue = ddlPDFs.SelectedValue;\n        if (!string.IsNullOrEmpty(selectedValue))\n        {\n            ltlPDF.Text = $"<object id='pdfViewer' data='{selectedValue}' type='application/pdf' width='100%' height='600px'>" +\n                          "This browser does not support PDFs. Please download the PDF to view it: " +\n                          $"<a href='{selectedValue}'>Download PDF</a>" +\n                          "</object>";\n        }\n        else\n        {\n            ltlPDF.Text = string.Empty;\n        }\n    }\n}