using System;\n   using System.Net.Mail;\n\n   public bool SendMail(MailMessage message)\n   {\n       message.From = new MailAddress(AppProperties.FromMailAddress, AppProperties.FromDisplayName);\n       SmtpClient smtp = new SmtpClient\n       {\n           EnableSsl = AppProperties.EnableSsl,\n           Host = "localhost",\n           Port = 25,\n           DeliveryMethod = SmtpDeliveryMethod.Network,\n           UseDefaultCredentials = true\n       };\n       try\n       {\n           smtp.Send(message);\n           return true;\n       }\n       catch (Exception ex)\n       {\n           // Log exception or handle it appropriately\n           Console.WriteLine("Exception caught in SendMail(): {0}", ex.ToString());\n           return false;\n       }\n   }