public static class HtmlHelperExtensions\n{\n    public static MvcHtmlString CustomTextBox(this HtmlHelper htmlHelper, string name, string value = "")\n    {\n        return new MvcHtmlString($"<input type=\"text\" name=\"{name}\" id=\"{name}\" value=\"{htmlEncode(value)}\">");\n    }\n\n    private static string htmlEncode(string value)\n    {\n        return string.IsNullOrEmpty(value) ? string.Empty : System.Web.HttpUtility.HtmlEncode(value);\n    }\n}