public static class SeedData\n{\n    public static async Task Initialize(IServiceProvider serviceProvider)\n    {\n        var roleManager = serviceProvider.GetRequiredService<RoleManager<IdentityRole>>();\n        var userManager = serviceProvider.GetRequiredService<UserManager<ApplicationUser>>();\n        \n        // Add roles\n        var adminRole = new IdentityRole("Admin");\n        await roleManager.CreateAsync(adminRole);\n        \n        // Add permissions\n        var permissions = new List<Permission>\n        {\n            new Permission { Name = "CreateProduct", ObjectType = "Products", AccessType = "CREATE" },\n            new Permission { Name = "ReadProduct", ObjectType = "Products", AccessType = "READ" },\n            new Permission { Name = "WriteProduct", ObjectType = "Products", AccessType = "WRITE" },\n            new Permission { Name = "DeleteProduct", ObjectType = "Products", AccessType = "DELETE" },\n        };\n        \n        // Add role-permission relationships\n        var adminPermissions = permissions.Select(p => new RolePermission { RoleId = adminRole.Id, Permission = p }).ToList();\n        \n        // Add users and assign roles\n        var adminUser = new ApplicationUser { UserName = "admin", Email = "admin@example.com", Role = adminRole };\n        await userManager.CreateAsync(adminUser, "AdminPassword123!");\n    }\n}