using WatiN.Core;\n   \n   public class SampleTest\n   {\n       [Test]\n       public void TestHomePage()\n       {\n           using (var browser = new IE("http://yourwebsite.com"))\n           {\n               Assert.IsTrue(browser.ContainsText("Welcome"));\n               \n               var loginLink = browser.Link(Find.ByText("Login"));\n               loginLink.Click();\n               \n               var usernameField = browser.TextField(Find.ByName("username"));\n               var passwordField = browser.TextField(Find.ByName("password"));\n               var loginButton = browser.Button(Find.ByValue("Login"));\n               \n               usernameField.TypeText("testuser");\n               passwordField.TypeText("password");\n               loginButton.Click();\n               \n               Assert.IsTrue(browser.ContainsText("Logout"));\n           }\n       }\n   }