using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TokenStorage\n{\n    // Encrypt the token\n    public static byte[] EncryptToken(string token)\n    {\n        byte[] tokenBytes = Encoding.UTF8.GetBytes(token);\n        return ProtectedData.Protect(tokenBytes, null, DataProtectionScope.CurrentUser);\n    }\n\n    // Decrypt the token\n    public static string DecryptToken(byte[] encryptedToken)\n    {\n        byte[] decryptedBytes = ProtectedData.Unprotect(encryptedToken, null, DataProtectionScope.CurrentUser);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n\n    public static void Main()\n    {\n        string token = "your_auth_token_here";\n        byte[] encryptedToken = EncryptToken(token);\n\n        // Save encryptedToken to a secure storage, like a file or registry\n\n        // Retrieve encryptedToken from secure storage and decrypt\n        string decryptedToken = DecryptToken(encryptedToken);\n        Console.WriteLine($"Decrypted Token: {decryptedToken}");\n    }\n}