public void CallRestService()\n{\n    var factory = new ChannelFactory<IMyService>("MyServiceEndpoint");\n    var client = factory.CreateChannel();\n\n    using (new OperationContextScope((IContextChannel)client))\n    {\n        client.PutMethod(myObject);\n\n        var response = OperationContext.Current.IncomingMessageProperties[HttpResponseMessageProperty.Name] as HttpResponseMessageProperty;\n        if (response != null)\n        {\n            int statusCode = (int)response.StatusCode;\n            Console.WriteLine("HTTP Status Code: " + statusCode);\n\n            if (statusCode == 200)\n            {\n                Console.WriteLine("Object was updated.");\n            }\n            else if (statusCode == 201)\n            {\n                Console.WriteLine("Object was created.");\n            }\n        }\n    }\n}