using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            var fromAddress = new MailAddress("your-email@gmail.com", "Your Name");\n            var toAddress = new MailAddress("recipient-email@example.com", "Recipient Name");\n            const string fromPassword = "your-email-password";\n            const string subject = "Subject of the email";\n            const string body = "Body of the email";\n\n            var smtp = new SmtpClient\n            {\n                Host = "smtp.gmail.com",\n                Port = 587,\n                EnableSsl = true,\n                DeliveryMethod = SmtpDeliveryMethod.Network,\n                UseDefaultCredentials = false,\n                Credentials = new NetworkCredential(fromAddress.Address, fromPassword)\n            };\n            using (var message = new MailMessage(fromAddress, toAddress)\n            {\n                Subject = subject,\n                Body = body\n            })\n            {\n                smtp.Send(message);\n            }\n            Console.WriteLine("Email sent successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Exception caught: {ex.Message}");\n        }\n    }\n}