using System;\nusing System.IO;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\npublic class MyUserControl : UserControl\n{\n    // Your custom user control code here\n}\n\npublic string RenderControlToHtml(UserControl control)\n{\n    using (StringWriter stringWriter = new StringWriter())\n    {\n        using (HtmlTextWriter htmlWriter = new HtmlTextWriter(stringWriter))\n        {\n            control.RenderControl(htmlWriter);\n            return stringWriter.ToString();\n        }\n    }\n}\n\n// Example usage\nUserControl myControl = new MyUserControl();\nmyControl.Controls.Add(new TextBox());\n\nstring controlHtml = RenderControlToHtml(myControl);\nConsole.WriteLine(controlHtml);