using System;\nusing System.Collections.Generic;\nusing System.Globalization;\n\nclass Program\n{\n    static void Main()\n    {\n        // Sample word list\n        string[] words = { "apple", "banana", "cherry", "date", "elderberry" };\n\n        // Create a comparer for "en-US" culture\n        StringComparer comparer = StringComparer.Create(new CultureInfo("en-US"), false);\n\n        // Sort the array using the comparer\n        Array.Sort(words, comparer);\n\n        // Perform a binary search using the same comparer\n        int index = Array.BinarySearch(words, "cherry", comparer);\n\n        Console.WriteLine("Sorted array:");\n        foreach (string word in words)\n        {\n            Console.WriteLine(word);\n        }\n\n        Console.WriteLine($"\nIndex of 'cherry': {index}");\n    }\n}