public partial class MainForm : Form\n{\n    public MainForm()\n    {\n        InitializeComponent();\n        \n        // Assuming you have a Panel named itemsPanel\n        itemsPanel.AutoScroll = true;\n\n        // Populate the panel with custom user controls\n        PopulateItems();\n    }\n\n    private void PopulateItems()\n    {\n        // Example data list\n        List<string> dataList = new List<string> { "Item 1", "Item 2", "Item 3" };\n\n        // Set the initial vertical position\n        int verticalPosition = 0;\n\n        foreach (var data in dataList)\n        {\n            MyCustomItem item = new MyCustomItem();\n            item.DataProperty = data; // Set your custom data property\n            item.Location = new Point(0, verticalPosition);\n            item.Width = itemsPanel.Width - 20; // Adjust width as needed\n            item.Anchor = AnchorStyles.Top | AnchorStyles.Left | AnchorStyles.Right;\n\n            // Add the user control to the panel\n            itemsPanel.Controls.Add(item);\n\n            // Update the vertical position for the next item\n            verticalPosition += item.Height + 5; // Adjust spacing as needed\n        }\n    }\n}