using mshtml;\n\n// Assuming 'element' is your HTMLAnchorElementClass object\nIHTMLElement htmlElement = element as IHTMLElement;\nif (htmlElement != null)\n{\n    IHTMLRect rect = htmlElement.getBoundingClientRect();\n    int top = rect.top;\n    int left = rect.left;\n\n    Console.WriteLine($"Top: {top}, Left: {left}");\n}\nelse\n{\n    Console.WriteLine("Element is not of type IHTMLElement");\n}