using System;\nusing FluentNHibernate.Cfg;\nusing FluentNHibernate.Cfg.Db;\nusing NHibernate.Tool.hbm2ddl;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        var cfg = Fluently.Configure()\n            .Database(MySQLConfiguration.Standard.ConnectionString("YourConnectionString"))\n            .Mappings(m => m.FluentMappings.AddFromAssemblyOf<YourEntityMap>())\n            .BuildConfiguration();\n\n        // Update schema without dropping existing tables\n        var schemaUpdate = new SchemaUpdate(cfg);\n        schemaUpdate.Execute(false, true);\n\n        // Your code to configure and use NHibernate session factory\n        var sessionFactory = cfg.BuildSessionFactory();\n\n        // Now you can use your sessionFactory to interact with the database\n    }\n}