using System;\nusing System.IO;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass LicenseService\n{\n    public static void Main()\n    {\n        TcpListener server = new TcpListener(IPAddress.Any, 13000);\n        server.Start();\n        Byte[] bytes = new Byte[256];\n        String data = null;\n\n        while (true)\n        {\n            TcpClient client = server.AcceptTcpClient();\n            NetworkStream stream = client.GetStream();\n\n            int i;\n            while ((i = stream.Read(bytes, 0, bytes.Length)) != 0)\n            {\n                data = System.Text.Encoding.ASCII.GetString(bytes, 0, i);\n                string result = RunLicenseGenerator(data);\n                byte[] msg = System.Text.Encoding.ASCII.GetBytes(result);\n                stream.Write(msg, 0, msg.Length);\n            }\n\n            client.Close();\n        }\n    }\n\n    private static string RunLicenseGenerator(string regName)\n    {\n        // Your logic to run the executable and get the result\n        System.Diagnostics.Process process = new System.Diagnostics.Process();\n        process.StartInfo.FileName = @"C:\inetpub\wwwroot\test.com\testLicense.exe";\n        process.StartInfo.Arguments = "\"" + regName + "\"";\n        process.StartInfo.UseShellExecute = false;\n        process.StartInfo.RedirectStandardOutput = true;\n        process.Start();\n\n        string result = process.StandardOutput.ReadToEnd();\n        process.WaitForExit();\n\n        return result;\n    }\n}