using System.Web.Http;\nusing MyApp.Core.Models;\nusing MyApp.Core.Repositories;\n\nnamespace MyApp.Api.Controllers\n{\n    public class WhateverController : ApiController\n    {\n        private readonly IWhateverRepository _repository;\n\n        public WhateverController(IWhateverRepository repository)\n        {\n            _repository = repository;\n        }\n\n        [HttpPost]\n        public IHttpActionResult SaveWhatever([FromBody] WhateverModel whatever)\n        {\n            _repository.Save(whatever);\n            return Ok();\n        }\n    }\n}