using System;\nusing System.Data.SqlClient;\n\nstring connectionString = "your_connection_string_here";\nstring query = "INSERT INTO upload_history (field1, field2, field3) VALUES (@field1, @field2, @field3)";\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    using (SqlCommand command = new SqlCommand(query, connection))\n    {\n        command.Parameters.AddWithValue("@field1", (object)varField1 ?? DBNull.Value);\n        command.Parameters.AddWithValue("@field2", (object)varField2 ?? DBNull.Value);\n        command.Parameters.AddWithValue("@field3", (object)varField3 ?? DBNull.Value);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}