using System;\nusing System.Data.SqlClient;\n\npublic void SaveTextboxEntries(int numberOfTextboxes, string[] textboxValues)\n{\n    string connectionString = "your_connection_string_here";\n    \n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        \n        // Insert into FormSubmissions and get the SubmissionID\n        string insertSubmissionQuery = "INSERT INTO FormSubmissions OUTPUT INSERTED.SubmissionID VALUES (DEFAULT)";\n        SqlCommand submissionCommand = new SqlCommand(insertSubmissionQuery, connection);\n        int submissionId = (int)submissionCommand.ExecuteScalar();\n\n        // Insert each textbox value into TextboxEntries\n        for (int i = 0; i < numberOfTextboxes; i++)\n        {\n            string insertTextboxQuery = "INSERT INTO TextboxEntries (SubmissionID, TextboxNumber, TextboxValue) VALUES (@SubmissionID, @TextboxNumber, @TextboxValue)";\n            SqlCommand textboxCommand = new SqlCommand(insertTextboxQuery, connection);\n            textboxCommand.Parameters.AddWithValue("@SubmissionID", submissionId);\n            textboxCommand.Parameters.AddWithValue("@TextboxNumber", i + 1);\n            textboxCommand.Parameters.AddWithValue("@TextboxValue", textboxValues[i]);\n            textboxCommand.ExecuteNonQuery();\n        }\n    }\n}