using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\nusing Xunit;\n\npublic class HtmlValidationTests\n{\n    [Fact]\n    public void HomePage_ShouldHaveValidHtml()\n    {\n        // Initialize the Chrome driver\n        using (IWebDriver driver = new ChromeDriver())\n        {\n            // Navigate to the ASP.NET page\n            driver.Navigate().GoToUrl("http://localhost:5000");\n\n            // Get the page source\n            string pageSource = driver.PageSource;\n\n            // Validate the HTML (you can use a library or an API for HTML validation)\n            ValidateHtml(pageSource);\n        }\n    }\n\n    private void ValidateHtml(string html)\n    {\n        // Implement your HTML validation logic here\n        // For example, use an API like W3C Validator API to validate the HTML\n        // or use a local HTML validation library\n        // Example using a hypothetical ValidateHtmlContent method\n        var result = HtmlValidator.ValidateHtmlContent(html);\n        Assert.True(result.IsValid, $"HTML validation failed: {result.Errors}");\n    }\n}