using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class ImageScaler\n{\n    public static Image GetScaledImage(Image originalImage)\n    {\n        // Get the graphics object from any control or form\n        using (Graphics g = Graphics.FromHwnd(IntPtr.Zero))\n        {\n            // Get the DPI of the device\n            float dpiX = g.DpiX;\n            float dpiY = g.DpiY;\n\n            // Determine the scale factor based on DPI\n            float scaleFactor = Math.Max(dpiX / 96.0f, dpiY / 96.0f);\n\n            // Calculate the new size\n            int newWidth = (int)(originalImage.Width * scaleFactor);\n            int newHeight = (int)(originalImage.Height * scaleFactor);\n\n            // Resize the image\n            Image scaledImage = new Bitmap(originalImage, new Size(newWidth, newHeight));\n            return scaledImage;\n        }\n    }\n}\n\n// Usage example\npublic class MainForm : Form\n{\n    private PictureBox pictureBox;\n\n    public MainForm()\n    {\n        pictureBox = new PictureBox();\n        this.Controls.Add(pictureBox);\n\n        Image originalImage = Image.FromFile("path_to_16x16_image.png");\n        pictureBox.Image = ImageScaler.GetScaledImage(originalImage);\n    }\n}