DataTable zipCodesTable;\n\n// Initialize the DataTable with columns\nvoid InitializeDataTable()\n{\n    zipCodesTable = new DataTable();\n    zipCodesTable.Columns.Add("ZipCode", typeof(string));\n    zipCodesTable.Columns.Add("Action", typeof(string)); // Track Add/Remove actions\n}\n\n// Bind DataTable to GridView\nvoid BindGridView()\n{\n    GridView2.DataSource = zipCodesTable;\n    GridView2.DataBind();\n}\n\n// Add selected zip code to DataTable\nvoid AddZipCode(string zipCode)\n{\n    DataRow row = zipCodesTable.NewRow();\n    row["ZipCode"] = zipCode;\n    row["Action"] = "Add";\n    zipCodesTable.Rows.Add(row);\n    BindGridView();\n}\n\n// Remove selected zip code from DataTable\nvoid RemoveZipCode(string zipCode)\n{\n    foreach (DataRow row in zipCodesTable.Rows)\n    {\n        if (row["ZipCode"].ToString() == zipCode)\n        {\n            row["Action"] = "Remove"; // Mark it for removal\n            break;\n        }\n    }\n    BindGridView();\n}\n\n// Save changes to database\nvoid SaveChanges()\n{\n    foreach (DataRow row in zipCodesTable.Rows)\n    {\n        string action = row["Action"].ToString();\n        string zipCode = row["ZipCode"].ToString();\n\n        if (action == "Add")\n        {\n            // Insert into database\n            SQLHelper.ExecuteNonQuery("INSERT INTO LocationZipCodes (LocationID, ZipCode) VALUES (@LocationID, @ZipCode)",\n                new SqlParameter("@LocationID", selectedLocationId),\n                new SqlParameter("@ZipCode", zipCode));\n        }\n        else if (action == "Remove")\n        {\n            // Delete from database\n            SQLHelper.ExecuteNonQuery("DELETE FROM LocationZipCodes WHERE LocationID = @LocationID AND ZipCode = @ZipCode",\n                new SqlParameter("@LocationID", selectedLocationId),\n                new SqlParameter("@ZipCode", zipCode));\n        }\n    }\n\n    // Clear the DataTable after saving changes\n    zipCodesTable.Clear();\n    BindGridView();\n}