using System;\nusing System.Collections.Generic;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = @"\n        <div>\n          <span><img class=""findme"" src=""something"" /></span>\n          <object class=""findme"" classid=""clsid:F08DF954-8592-11D1-B16A-00C0F0283628"" id=""Slider1"" width=""100"" height=""50"">\n            <param name=""BorderStyle"" value=""1"" />\n            <param name=""MousePointer"" value=""0"" />\n            <param name=""Enabled"" value=""1"" />\n            <param name=""Min"" value=""0"" />\n            <param name=""Max"" value=""10"" />\n          </object>\n        </div>";\n\n        string pattern = @"<(\w+)([^>]*\sclass\s*=\s*""[^""]*\bfindme\b[^""]*""[^>]*)>(.*?)<\/\1>|<(\w+)([^>]*\sclass\s*=\s*""[^""]*\bfindme\b[^""]*""[^>]*)\/>";\n\n        Regex regex = new Regex(pattern, RegexOptions.Singleline);\n        MatchCollection matches = regex.Matches(html);\n\n        List<string> results = new List<string>();\n\n        foreach (Match match in matches)\n        {\n            if (match.Groups[0].Success)\n            {\n                results.Add(match.Groups[0].Value);\n            }\n        }\n\n        foreach (var result in results)\n        {\n            Console.WriteLine(result);\n            Console.WriteLine();\n        }\n    }\n}