using System;\nusing System.IO;\nusing System.Web;\n\npublic partial class DownloadFile : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string fileId = Request.QueryString["fileId"];\n        string filePath = GetFilePathFromId(fileId);\n\n        if (File.Exists(filePath))\n        {\n            ServeFile(filePath);\n        }\n        else\n        {\n            Response.StatusCode = 404;\n            Response.End();\n        }\n    }\n\n    private string GetFilePathFromId(string fileId)\n    {\n        // Implement logic to map fileId to the actual file path\n        return $@"\\your-server\your-share\{fileId}.pdf";\n    }\n\n    private void ServeFile(string filePath)\n    {\n        Response.ContentType = "application/octet-stream";\n        Response.AppendHeader("Content-Disposition", $"attachment; filename={Path.GetFileName(filePath)}");\n        Response.TransmitFile(filePath);\n        Response.End();\n    }\n}