using System;\nusing System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\n\nclass Program\n{\n    static void Main()\n    {\n        string pdfPath = @"path\to\your\pdf\file.pdf";\n        string searchText = "your search string";\n\n        if (IsTextPresentInPdf(pdfPath, searchText))\n        {\n            Console.WriteLine($"The text '{searchText}' was found in the PDF.");\n        }\n        else\n        {\n            Console.WriteLine($"The text '{searchText}' was not found in the PDF.");\n        }\n    }\n\n    static bool IsTextPresentInPdf(string pdfPath, string searchText)\n    {\n        using (PdfReader reader = new PdfReader(pdfPath))\n        {\n            for (int i = 1; i <= reader.NumberOfPages; i++)\n            {\n                string pageText = PdfTextExtractor.GetTextFromPage(reader, i);\n                if (pageText.Contains(searchText, StringComparison.OrdinalIgnoreCase))\n                {\n                    return true;\n                }\n            }\n        }\n        return false;\n    }\n}