using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string phpPath = @"C:\xampp\php\php.exe";\n        string scriptPath = @"path\to\your\script.php"; // Update this to the actual script path\n\n        var p = new Process\n        {\n            StartInfo = new ProcessStartInfo(phpPath, scriptPath)\n            {\n                RedirectStandardOutput = true,\n                RedirectStandardError = true,\n                UseShellExecute = false,\n                CreateNoWindow = true\n            }\n        };\n\n        var output = new StringWriter();\n        var error = new StringWriter();\n\n        p.OutputDataReceived += (sender, eventArgs) => output.WriteLine(eventArgs.Data);\n        p.ErrorDataReceived += (sender, eventArgs) => error.WriteLine(eventArgs.Data);\n\n        try\n        {\n            p.Start();\n            p.BeginOutputReadLine();\n            p.BeginErrorReadLine();\n            p.WaitForExit();\n\n            if (p.ExitCode != 0)\n            {\n                string errorMsg = error.GetStringBuilder().ToString();\n                throw new Exception($"PHP failed with the following output:{Environment.NewLine}{errorMsg}");\n            }\n\n            string res = output.GetStringBuilder().ToString();\n            Console.WriteLine(res);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n            Console.WriteLine("Standard Error Output: " + error.GetStringBuilder().ToString());\n        }\n        finally\n        {\n            p.Close();\n        }\n    }\n}