using Microsoft.AspNetCore.Mvc;\n   using Twilio.AspNet.Core;\n   using Twilio.TwiML;\n   using System.Data.SqlClient;\n\n   [Route("api/[controller]")]\n   [ApiController]\n   public class SmsController : TwilioController\n   {\n       [HttpPost]\n       public IActionResult ReceiveSms([FromForm] string From, [FromForm] string Body)\n       {\n           // Extract information from the SMS\n           var phoneNumber = From;\n           var messageBody = Body;\n\n           // Update the SQL database\n           var connectionString = "Your SQL Server connection string here";\n           using (var connection = new SqlConnection(connectionString))\n           {\n               connection.Open();\n               var query = "INSERT INTO Deliveries (PhoneNumber, Message) VALUES (@PhoneNumber, @Message)";\n               using (var command = new SqlCommand(query, connection))\n               {\n                   command.Parameters.AddWithValue("@PhoneNumber", phoneNumber);\n                   command.Parameters.AddWithValue("@Message", messageBody);\n                   command.ExecuteNonQuery();\n               }\n           }\n\n           // Respond to Twilio\n           var response = new MessagingResponse();\n           response.Message("Thank you for your update!");\n\n           return TwiML(response);\n       }\n   }