using System;\nusing System.Web;\nusing System.Text.RegularExpressions;\n\npublic class UrlRewriteModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication application = (HttpApplication)sender;\n        HttpContext context = application.Context;\n        string requestedUrl = context.Request.Url.AbsolutePath.ToLower();\n\n        // Regex to match the desired URL pattern\n        Regex regex = new Regex(@"^/content/articles/(\d+)/(.+)$");\n        Match match = regex.Match(requestedUrl);\n\n        if (match.Success)\n        {\n            string id = match.Groups[1].Value;\n            context.RewritePath($"/viewContent.aspx?Id={id}");\n        }\n    }\n\n    public void Dispose() { }\n}