public class Parent\n{\n    [Column(IsPrimaryKey = true)]\n    public int Id { get; set; }\n\n    private EntityRef<Child> _child;\n    [Association(Storage = "_child", ThisKey = "Id", OtherKey = "ParentId", IsForeignKey = true, IsUnique = true)]\n    public Child Child\n    {\n        get { return _child.Entity; }\n        set { _child.Entity = value; }\n    }\n}\n\npublic class Child\n{\n    [Column(IsPrimaryKey = true)]\n    public int ParentId { get; set; }\n\n    private EntityRef<Parent> _parent;\n    [Association(Storage = "_parent", ThisKey = "ParentId", OtherKey = "Id", IsForeignKey = true)]\n    public Parent Parent\n    {\n        get { return _parent.Entity; }\n        set { _parent.Entity = value; }\n    }\n}