using System;\nusing System.Diagnostics;\n\npublic class RegSqlHelper\n{\n    public static void ExecuteRegSql(string arguments)\n    {\n        try\n        {\n            ProcessStartInfo startInfo = new ProcessStartInfo\n            {\n                FileName = "aspnet_regsql.exe",\n                Arguments = arguments,\n                RedirectStandardOutput = true,\n                UseShellExecute = false,\n                CreateNoWindow = true\n            };\n\n            using (Process process = Process.Start(startInfo))\n            {\n                using (System.IO.StreamReader reader = process.StandardOutput)\n                {\n                    string result = reader.ReadToEnd();\n                    Console.WriteLine(result);\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        // Example arguments for aspnet_regsql.exe, customize as needed\n        string arguments = "-S localhost -E -d MyDatabase -A all";\n        ExecuteRegSql(arguments);\n    }\n}