using System;\nusing System.Data.SqlClient;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string query = "SELECT [ColumnName] FROM [TableName] WHERE [Condition]"; // Adjust the query to your needs\n        string outputPath = "C:\\path\\to\\output\\file.pdf"; // Adjust the output path\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    if (reader.Read())\n                    {\n                        byte[] fileData = (byte[])reader["ColumnName"];\n                        File.WriteAllBytes(outputPath, fileData);\n                        Console.WriteLine("File written successfully to " + outputPath);\n                    }\n                }\n            }\n        }\n    }\n}