using System;\nusing System.Drawing;\nusing System.Threading.Tasks;\nusing System.Windows.Forms;\n\npublic class ZoomPictureBox : PictureBox\n{\n    private Bitmap originalImage;\n\n    public ZoomPictureBox()\n    {\n        this.DoubleBuffered = true;\n    }\n\n    public void SetImage(Bitmap image)\n    {\n        this.originalImage = image;\n        this.Image = image;\n    }\n\n    public async void ScaleImageAsync(float scaleFactor)\n    {\n        if (originalImage == null)\n            return;\n\n        await Task.Run(() => {\n            Bitmap scaledImage = new Bitmap(\n                (int)(originalImage.Width * scaleFactor),\n                (int)(originalImage.Height * scaleFactor));\n\n            using (Graphics g = Graphics.FromImage(scaledImage))\n            {\n                g.DrawImage(originalImage, new Rectangle(0, 0, scaledImage.Width, scaledImage.Height));\n            }\n\n            // Update the UI thread with the new image\n            this.Invoke((Action)(() => {\n                this.Image = scaledImage;\n            }));\n        });\n    }\n}