using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument xmlDocument = new XmlDocument();\n        xmlDocument.Load("path_to_your_xml_file.xml");\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(xmlDocument.NameTable);\n        nsmgr.AddNamespace("ns", "urn:schemas-ncr-com:ECPIX:CXF:FileStructure:020001");\n\n        XmlNode imageViewDetailNode = xmlDocument.SelectSingleNode("//ns:Item/ns:ImageViewDetail", nsmgr);\n\n        if (imageViewDetailNode != null)\n        {\n            Console.WriteLine("ImageViewDetail element found!");\n        }\n        else\n        {\n            Console.WriteLine("ImageViewDetail element not found.");\n        }\n    }\n}