using HtmlAgilityPack;\nusing System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        var url = "http://example.com";\n        var web = new HtmlWeb();\n        var doc = web.Load(url);\n\n        // Example: Select all 'a' tags\n        var nodes = doc.DocumentNode.SelectNodes("//a");\n        if (nodes != null)\n        {\n            foreach (var node in nodes)\n            {\n                Console.WriteLine(node.InnerText);\n            }\n        }\n    }\n}